from django.contrib import adminfrom .models.demos import Demo, Demodayfrom .models.users import Student, ProjectTeamfrom .models.companies import Representative, Companyfrom .models.techs import Techfrom .filters.admin.filters import UpcomingOrPastDemodaysFilter, \                                   HowManyDemosDidTakeFilterclass DemoInLine(admin.StackedInline):    model = Demo    extra = 0class RepresentativeInLine(admin.StackedInline):    model = Representative    extra = 0class StudentInLine(admin.StackedInline):    model = Student    extra = 0class TechInLine(admin.StackedInline):    model = Tech    extra = 0@admin.register(Demoday)class DemodayAdmin(admin.ModelAdmin):    list_filter = (UpcomingOrPastDemodaysFilter,)    list_display = ('company', 'planned_date', 'did_take_place',)    fieldsets = (        (None, {'fields': ['company', 'planned_date', 'did_take_place',                           'demos']}),    )    # inlines = (DemoInLine,)@admin.register(Demo)class DemoAdmin(admin.ModelAdmin):    pass@admin.register(Company)class CompaniesAdmin(admin.ModelAdmin):    search_fields = ('name',)    list_display = ('name', 'open_positions')    fieldsets = (        (None, {'fields': ['name', 'website', 'open_positions', 'sought_techs',                           'short_description']}),    )    inlines = (RepresentativeInLine,)@admin.register(ProjectTeam)class ProjectTeamAdmin(admin.ModelAdmin):    pass@admin.register(Student)class StudentAdmin(admin.ModelAdmin):    list_filter = ('module_in_school', HowManyDemosDidTakeFilter)    list_display = ('full_name', 'is_obliged_to_present',)@admin.register(Tech)class TechAdmin(admin.ModelAdmin):    pass